import bus from "../common/bus";
import API from "@/api";
import Vue from "vue";
export default {
  name: "dashboard",
  data: function data() {
    this.chartSettings = {
      labelMap: {
        PowerTotal: "同安用电总量(KWh)"
      }
    };
    return {
      recordTotal: "",
      userTotal: "",
      lastIP: "",
      lastTime: "",
      name: localStorage.getItem("username"),
      chartData: [],
      todoList: [{
        title: "7.30号更新",
        status: false
      }],
      data: []
    };
  },
  components: {},
  computed: {
    role: function role() {
      return localStorage.getItem("rolename");
    }
  },
  created: function created() {
    this.getTimeAndIp();
    this.fillData();
    this.handleListener();
  },
  activated: function activated() {
    this.handleListener();
  },
  deactivated: function deactivated() {
    window.removeEventListener("resize", this.renderChart);
    bus.$off("collapse", this.handleBus);
  },
  methods: {
    getTimeAndIp: function getTimeAndIp() {
      var _this = this;

      var params = {
        geNumber: localStorage.getItem("username")
      };
      API.userUtil.getTimeAndIp(params).then(function (_ref) {
        var data = _ref.data;

        if (data.status == 0) {
          _this.lastIP = data.data.ip;
          _this.lastTime = data.data.time;
          _this.recordTotal = data.data.recordTotal;
          _this.userTotal = data.data.userTotal;
        } else {
          //this.$Message.error(data.msg);
          _this.$message("请求失败！");
        }
      }).catch(function (data) {
        _this.$message("请求失败！");

        console.log(data);
      });
    },
    fillData: function fillData() {
      var _this2 = this;

      API.powerUtil.getTotalPowerByWeek().then(function (_ref2) {
        var data = _ref2.data;

        if (data.status == 0) {
          _this2.chartData = {
            columns: ["Time", "PowerTotal"],
            rows: data.data.list
          };
        } else {
          _this2.$message("请求失败！");
        }
      }).catch(function (data) {
        _this2.$message("请求失败ww！");

        console.log(data);
      });
    },
    changeDate: function changeDate() {
      var now = new Date().getTime();
      this.data.forEach(function (item, index) {
        var date = new Date(now - (6 - index) * 86400000);
        item.name = "".concat(date.getFullYear(), "/").concat(date.getMonth() + 1, "/").concat(date.getDate());
      });
    },
    handleListener: function handleListener() {
      bus.$on("collapse", this.handleBus);
    },
    handleBus: function handleBus(msg) {
      var _this3 = this;

      setTimeout(function () {
        _this3.renderChart();
      }, 300);
    },
    renderChart: function renderChart() {//this.$refs.bar.renderChart();
      // this.$refs.line.renderChart();
    }
  }
};
