import API from "@/api";
import Vue from "vue";
export default {
  data: function data() {
    return {
      navselected: "",
      beginTime: "",
      endTime: "",
      powerType: "ZxygZ",
      Source: "所有数据来源",
      //表格当前页数据
      tableData: [],
      //多选数组
      multipleSelection: [],
      //搜索条件
      criteria: "",
      //下拉菜单选项
      select: "",
      //默认每页数据量
      pagesize: 50000000,
      //默认高亮行数据id
      highlightId: -1,
      //当前页码
      currentPage: 1,
      //查询的页码
      start: 1,
      //默认数据总数
      totalCount: 0,
      formLabelWidth: "120px",
      list: "",
      key: "",
      EStationName: ""
    };
  },
  created: function created() {
    this.loadMenu();
  },
  methods: {
    //获取所选节点
    selectMenu: function selectMenu(key, keyPath, name) {
      this.EStationName = keyPath[1].split("/")[1];

      if (keyPath.length <= 2) {
        this.key = "";
      } else {
        this.key = keyPath[2].split("/")[1];
      }

      console.log(key + "---" + keyPath + '---' + name);
    },
    handleOpen: function handleOpen(key, keyPath, name) {
      this.EStationName = key.split("/")[1];
      this.key = "";
      console.log(this.EStationName);
      console.log("open----" + key + ",,,," + keyPath + ',,,,,' + name);
      this.navselected = "/";
      console.log(this.navselected);
    },
    handleClose: function handleClose(key, keyPath, name) {
      this.EStationName = key.split("/")[1];
      this.key = "";
      console.log(this.EStationName);
      this.navselected = "/";
      console.log("close----" + key + ",,,," + keyPath + ',,,,,' + name);
      console.log(this.navselected);
    },
    //加载树状图
    loadMenu: function loadMenu() {
      var _this = this;

      API.powerUtil.getMenu().then(function (_ref) {
        var data = _ref.data;
        console.log(data.data);

        if (data.status == 0) {
          _this.list = data.data;
        } else {
          //this.$Message.error(data.msg);
          _this.$message("请求失败！");
        }
      }).catch(function (data) {
        _this.$message("请求失败ww！");

        console.log(data);
      });
    },
    //从服务器读取数据
    getPowerData: function getPowerData() {
      var _this2 = this;

      if (this.beginTime == "") {
        this.$message("请选择开始时间！");
      } else if (this.endTime == "") {
        this.$message("请选择结束时间！");
      } else if (this.Source == "") {
        this.$message("请选择数据来源！");
      } else if (this.powerType == "") {
        this.$message("请选择功率类型！");
      } else {
        var params = {
          estationName: this.EStationName,
          emeterName: this.key,
          beginTime: this.beginTime,
          endTime: this.endTime,
          source: this.Source,
          powerType: this.powerType,
          pageNum: this.currentPage,
          pageSize: this.pagesize
        };
        API.powerUtil.getPowerData(params).then(function (_ref2) {
          var data = _ref2.data;
          console.log(data.data);

          if (data.status == 0) {
            if (data.data.tableData == null) {
              _this2.tableData = null;
              _this2.totalCount = 0;
            } else {
              _this2.tableData = data.data.tableData.list;
              _this2.totalCount = data.data.tableData.total;
            }
          } else {
            _this2.$message("请求失败！");
          }
        }).catch(function (data) {
          _this2.$message("请求失败ww！");

          console.log(data);
        });
      }
    },
    //多选响应
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    //点击行响应
    handleclick: function handleclick(row, event, column) {
      this.highlightId = row.id;
    },
    //搜索
    search: function search() {
      console.log(this.select);
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    },
    add: function add() {
      this.addFormVisible = true;
    },
    exportPowerTotal: function exportPowerTotal() {
      if (this.EStationName == null || this.EStationName == "") {
        this.$message("请选择电表！");
      } else if (this.beginTime == "") {
        this.$message("请选择开始时间！");
      } else if (this.endTime == "") {
        this.$message("请选择结束时间！");
      } else if (this.powerType == "") {
        this.$message("请选择功率类型！");
      } else {
        window.location.href = "http://localhost:8088/api/exportPowerTotal.do?estationName=" + this.EStationName + "&emeterName=" + this.key + "&beginTime=" + this.beginTime + "&endTime=" + this.endTime + "&powerType=" + this.powerType; // window.location.href = "http://10.30.100.110:8088/api/exportPowerTotal.do?estationName=" + this.EStationName+"&emeterName="+this.key+"&beginTime="+this.beginTime+"&endTime="+this.endTime+"&powerType="+this.powerType;
      } // this.$message("待开发");

    },
    exportAllPowerTotal: function exportAllPowerTotal() {
      if (this.beginTime == "") {
        this.$message("请选择开始时间！");
      } else if (this.endTime == "") {
        this.$message("请选择结束时间！");
      } else if (this.powerType == "") {
        this.$message("请选择功率类型！");
      } else {
        window.location.href = "http://localhost:8088/api/exportPowerTotal.do?beginTime=" + this.beginTime + "&endTime=" + this.endTime + "&powerType=" + this.powerType; //window.location.href = "http://10.30.100.110:8088/api/exportPowerTotal.do?beginTime="+this.beginTime+"&endTime="+this.endTime+"&powerType="+this.powerType;
      } // this.$message("待开发");

    },
    //改变当前点击的行的class，高亮当前行
    tableRowClassName: function tableRowClassName(row, index) {
      if (row.id == this.highlightId) {
        return "info-row";
      }
    },
    //每页显示数据量变更
    handleSizeChange: function handleSizeChange(val) {
      this.pagesize = val;
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    },
    //页码变更
    handleCurrentChange: function handleCurrentChange(val) {
      this.currentPage = val;
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    }
  }
};
