import API from "@/api";
import Vue from "vue";
import "v-charts/lib/style.css";
import "echarts/lib/component/markLine";
import "echarts/lib/component/markPoint";
import "echarts/lib/component/dataZoom";
import axios from "axios";
export default {
  data: function data() {
    this.dataZoom = [{
      type: "slider",
      start: 0,
      end: 100
    }];
    this.markLine = {
      data: [{
        name: "平均线",
        type: "average"
      }]
    };
    this.markPoint = {
      data: [{
        name: "最大值",
        type: "max"
      }, {
        name: "最小值",
        type: "min"
      }]
    };
    this.toolbox = {
      feature: {
        magicType: {
          type: ["line", "bar"]
        },
        saveAsImage: {}
      }
    }, this.chartSettings = {
      labelMap: {
        peak: "峰",
        flat: "平",
        ravine: "谷",
        peakPercent: "峰占比"
      },
      axisSite: {
        right: ["peakPercent"]
      },
      yAxisType: ["KMB", "percent"],
      yAxisName: ["数值", "百分比"]
    };
    return {
      beginTime: "",
      endTime: "",
      chartData: null,
      dataEmpty: true,
      dataSource: "",
      //表格当前页数据
      tableData: [],
      //多选数组
      multipleSelection: [],
      //搜索条件
      criteria: "",
      //下拉菜单选项
      select: "",
      //默认每页数据量
      pagesize: 1000,
      //默认高亮行数据id
      highlightId: -1,
      //当前页码
      currentPage: 1,
      //查询的页码
      start: 1,
      //默认数据总数
      totalCount: 0,
      formLabelWidth: "120px",
      //树状图列表
      list: "",
      //电量数据
      data: [],
      //间隔时间类型
      dateType: "hour",
      //图形类型
      chartType: "bar",
      //所选电表
      key: "",
      height: 20,
      Title: "Title",
      EStationName: ""
    };
  },
  components: {},
  created: function created() {
    this.loadMenu();
  },
  methods: {
    //从服务器读取数据
    selectMenu: function selectMenu(key, keyPath, name) {
      this.EStationName = keyPath[1].split("/")[1];
      console.log(keyPath.length);

      if (keyPath.length <= 2) {
        this.key = "";
      } else {
        this.key = keyPath[2].split("/")[1];
      }
    },
    //加载树状图
    loadMenu: function loadMenu() {
      var _this = this;

      API.powerUtil.getMenu().then(function (_ref) {
        var data = _ref.data;
        console.log(data.data);

        if (data.status == 0) {
          _this.list = data.data;
        } else {
          //this.$Message.error(data.msg);
          _this.$message("请求失败！");
        }
      }).catch(function (data) {
        _this.$message("请求失败ww！");

        console.log(data);
      });
    },
    increaseHeight: function increaseHeight() {
      this.height += 10;
    },
    //填充图表数据
    fillData: function fillData(data) {
      this.chartData = {
        columns: ["TimeTag", "peak", "flat", "ravine", "peakPercent"],
        rows: data
      };

      if (data.length == 0) {
        this.dataEmpty = true;
      } else {
        this.dataEmpty = false;
      }
    },
    getPowerData: function getPowerData() {
      var _this2 = this;

      if (this.key == null || this.key == "") {
        this.$message("请选择电表！");
      } else if (this.beginTime == "") {
        this.$message("请选择开始时间！");
      } else if (this.dateType == "") {
        this.$message("请选择时间间隔");
      } else if (this.chartType == "") {
        this.$message("请选择图形！");
      } else {
        if (this.dateType == "hour") {
          this.Title = this.key + "：24小时电量(" + this.dateFormat(this.beginTime) + ")";
        } else if (this.dateType == "day") {
          this.Title = this.key + "：30天电量(" + this.dateFormat(this.beginTime) + ")";
        }

        var params = {
          estationName: this.EStationName,
          emeterName: this.key,
          beginTime: this.beginTime,
          dateType: this.dateType,
          pageNum: this.currentPage,
          pageSize: this.pagesize
        };
        API.peakUtil.getPowerData(params).then(function (_ref2) {
          var data = _ref2.data;

          if (data.status == 0) {
            _this2.tableData = data.data.data.page.list;
            _this2.totalCount = data.data.data.page.total;

            _this2.fillData(data.data.data.page.list);
          } else {
            _this2.$message("请求失败！");
          }
        }).catch(function (data) {
          _this2.$message("请求失败ww！");

          console.log(data);
        });
      }
    },
    //多选响应
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    //点击行响应
    handleclick: function handleclick(row, event, column) {
      this.highlightId = row.id;
    },
    exportData: function exportData() {
      // window.location.href = "http://10.30.100.110:8088/api/downUser.do";
      if (this.key == null || this.key == "") {
        this.$message("请选择电表！");
      } else if (this.beginTime == "") {
        this.$message("请选择开始时间！");
      } else if (this.dateType == "") {
        this.$message("请选择时间间隔");
      } else {
        var params = {
          estationName: this.EStationName,
          emeterName: this.key,
          beginTime: this.beginTime,
          dateType: this.dateType
        }; //window.location.href = "http://localhost:8088/api/power/exportPower.do?estationName=" + this.EStationName+"&emeterName="+this.key+"&beginTime="+this.beginTime+"&dateType="+this.dateType

        window.location.href = "http://10.30.100.110:8088/api/power/exportPower.do?estationName=" + this.EStationName + "&emeterName=" + this.key + "&beginTime=" + this.beginTime + "&dateType=" + this.dateType;
      } //this.$message("待开发");

    },
    exportAllData: function exportAllData() {
      if (this.beginTime == "") {
        this.$message("请选择开始时间！");
      } else {
        var month1 = API.userUtil.formatTimeToStr(this.beginTime, "yyyy-M");
        console.log(month1);
        window.location.href = "http://10.30.100.110:8088/api/power/exportAllPowerAnalyze.do?month=" + month1.toString(); // window.location.href = "http://10.30.100.126:8088/api/power/exportAllPowerAnalyze.do?month="+month1.toString();
      }
    },
    //改变当前点击的行的class，高亮当前行
    tableRowClassName: function tableRowClassName(row, index) {
      if (row.id == this.highlightId) {
        return "info-row";
      }
    },
    //每页显示数据量变更
    handleSizeChange: function handleSizeChange(val) {
      this.pagesize = val;
      this.getPowerData();
    },
    //页码变更
    handleCurrentChange: function handleCurrentChange(val) {
      this.currentPage = val;
      this.getPowerData();
    },
    //时间格式化函数，此处仅针对yyyy-MM-dd hh:mm:ss 的格式进行格式化
    dateFormat: function dateFormat(time) {
      var date = new Date(time);
      var year = date.getFullYear();
      /* 在日期格式中，月份是从0开始的，因此要加0
       * 使用三元表达式在小于10的前面加0，以达到格式统一  如 09:11:05
       * */

      var month = date.getMonth() + 1 < 10 ? "0" + (date.getMonth() + 1) : date.getMonth() + 1;
      var day = date.getDate() < 10 ? "0" + date.getDate() : date.getDate();
      var hours = date.getHours() < 10 ? "0" + date.getHours() : date.getHours();
      var minutes = date.getMinutes() < 10 ? "0" + date.getMinutes() : date.getMinutes();
      var seconds = date.getSeconds() < 10 ? "0" + date.getSeconds() : date.getSeconds(); // 拼接

      return year + "-" + month + "-" + day;
    }
  },
  computed: {
    myStyles: function myStyles() {
      return {
        height: "".concat(this.height, "px"),
        position: "relative"
      };
    }
  }
};
