import API from "@/api";
import Vue from "vue";
export default {
  data() {
    return {
      navselected:"",
      beginTime: "",
      endTime: "",
      powerType: "ZxygZ",
      Source: "所有数据来源",
      //表格当前页数据
      tableData: [],
      //多选数组
      multipleSelection: [],
      //搜索条件
      criteria: "",
      //下拉菜单选项
      select: "",
      //默认每页数据量
      pagesize: 50000000,
      //默认高亮行数据id
      highlightId: -1,
      //当前页码
      currentPage: 1,
      //查询的页码
      start: 1,
      //默认数据总数
      totalCount: 0,
      formLabelWidth: "120px",
      list: "",
      key: "",
      EStationName:""
    };
  },
  created() {
    this.loadMenu();
  },
  methods: {
    //获取所选节点
    selectMenu: function(key, keyPath, name) {
      this.EStationName = keyPath[1].split("/")[1];
      if(keyPath.length<=2){
        this.key = "";
      }else{
        this.key = keyPath[2].split("/")[1];
      }
      console.log(key+"---"+keyPath+'---'+name);
    },
    handleOpen: function(key, keyPath, name) {
      this.EStationName = key.split("/")[1];
      this.key = "";
      console.log(this.EStationName);
      console.log("open----"+key+",,,,"+keyPath+',,,,,'+name);
      this.navselected="/";
      console.log(this.navselected);
    },
    handleClose: function(key, keyPath, name) {
      this.EStationName = key.split("/")[1];
      this.key = "";
      console.log(this.EStationName);
      this.navselected="/";
      console.log("close----"+key+",,,,"+keyPath+',,,,,'+name);
      console.log(this.navselected);
    },

    //加载树状图
    loadMenu: function() {
      API.powerUtil
        .getMenu()
        .then(({ data }) => {
          console.log(data.data);
          if (data.status == 0) {
            this.list = data.data;
          } else {
            //this.$Message.error(data.msg);
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败ww！");
          console.log(data);
        });
    },
    //从服务器读取数据
    getPowerData: function() {
       if(this.beginTime==""){
        this.$message("请选择开始时间！");
      }else if(this.endTime==""){
        this.$message("请选择结束时间！");
      }else if(this.Source==""){
        this.$message("请选择数据来源！");
      }else if(this.powerType==""){
        this.$message("请选择功率类型！");
      }else{
      let params = {
        estationName:this.EStationName,
        emeterName: this.key,
        beginTime: this.beginTime,
        endTime: this.endTime,
        source: this.Source,
        powerType: this.powerType,
        pageNum: this.currentPage,
        pageSize: this.pagesize
      };
      API.powerUtil
        .getPowerData(params)
        .then(({ data }) => {
          console.log(data.data);
          if (data.status == 0) {
            if(data.data.tableData==null){
              this.tableData = null;
              this.totalCount = 0;
            }else{
            this.tableData = data.data.tableData.list;
            this.totalCount = data.data.tableData.total;
            }

          } else {
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败ww！");
          console.log(data);
        });
      }
    },

    //多选响应
    handleSelectionChange: function(val) {
      this.multipleSelection = val;
    },

    //点击行响应
    handleclick: function(row, event, column) {
      this.highlightId = row.id;
    },

    //搜索
    search: function() {
      console.log(this.select);
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    },
    add: function() {
      this.addFormVisible = true;
    },
    exportPowerTotal() {
      if(this.EStationName==null || this.EStationName ==""){
         this.$message("请选择电表！");
      }else if(this.beginTime==""){
        this.$message("请选择开始时间！");
      }else if(this.endTime==""){
        this.$message("请选择结束时间！");
      }else if(this.powerType==""){
        this.$message("请选择功率类型！");
      }else{
          window.location.href = "http://47.106.172.176:8089/api/power/exportPowerTotal.do?estationName=" + this.EStationName+"&emeterName="+this.key+"&beginTime="+this.beginTime+"&endTime="+this.endTime+"&powerType="+this.powerType;
         //window.location.href = "http://10.30.100.110:8088/api/power/exportPowerTotal.do?estationName=" + this.EStationName+"&emeterName="+this.key+"&beginTime="+this.beginTime+"&endTime="+this.endTime+"&powerType="+this.powerType;
      }

     // this.$message("待开发");
    },
    exportAllPowerTotal() {
      if(this.beginTime==""){
        this.$message("请选择开始时间！");
      }else if(this.endTime==""){
        this.$message("请选择结束时间！");
      }else if(this.powerType==""){
        this.$message("请选择功率类型！");
      }else{
        //window.location.href = "http://localhost:8088/api/power/exportPowerTotal.do?beginTime="+this.beginTime+"&endTime="+this.endTime+"&powerType="+this.powerType;
        window.location.href = "http://47.106.172.176:8089/api/power/exportPowerTotal.do?beginTime="+this.beginTime+"&endTime="+this.endTime+"&powerType="+this.powerType;
      }
    },

    //改变当前点击的行的class，高亮当前行
    tableRowClassName: function(row, index) {
      if (row.id == this.highlightId) {
        return "info-row";
      }
    },

    //每页显示数据量变更
    handleSizeChange: function(val) {
      this.pagesize = val;
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    },

    //页码变更
    handleCurrentChange: function(val) {
      this.currentPage = val;
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    }
  }
};
