import bus from "../common/bus";
import API from "@/api";
import Vue from "vue";
export default {
  name: "dashboard",
  data() {
    this.chartSettings = {
      labelMap: {
        PowerTotal: "同安用电总量(KWh)"
      }
    };

    return {
      recordTotal: "",
      userTotal: "",
      lastIP: "",
      lastTime: "",
      name: this.$cookie.get("username"),
      chartData: "",
      todoList: [
        {
          title: "5.1-5.10 完成电量数据查询模块开发",
          status: false
        },
        {
          title: "5.10-6.15 完成瞬时量分析模块开发",
          status: false
        },
        {
          title: "6.1-6.30 完成电量数据分析模块开发",
          status: false
        },
        {
          title: "7.1-7.31 完成采集状态模块开发",
          status: false
        },
        {
          title: "8.1-8.31 完成手动采集操作模块开发",
          status: false
        },
        {
          title: "9.1-9.30 完成采集历史数据查看模块开发",
          status: false
        },
        {
          title: "10.1-10.31 完成基础信息维护模块开发",
          status: false
        },
        {
          title: "11.1-11.15 完成系统的整体测试",
          status: false
        }
      ],
      data: []
    };
  },
  components: {},
  computed: {
    role() {
      return this.$cookie.get("role") === "admin" ? "管理员" : "普通用户";
    }
  },
  created() {
    this.getTimeAndIp();
    this.fillData();
    this.handleListener();
  },
  activated() {
    this.handleListener();
  },
  deactivated() {
    window.removeEventListener("resize", this.renderChart);
    bus.$off("collapse", this.handleBus);
  },
  methods: {
    getTimeAndIp: function() {
      let params = {
        geNumber: this.$cookie.get("username")
      };
      API.userUtil
        .getTimeAndIp(params)
        .then(({ data }) => {
          console.log(data);
          if (data.status == 0) {
            this.lastIP = data.data.ip;
            this.lastTime = data.data.time;
            this.recordTotal = data.data.recordTotal;
            this.userTotal = data.data.userTotal;
          } else {
            //this.$Message.error(data.msg);
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败ww！");
          console.log(data);
        });
    },

    fillData: function() {
      API.powerUtil
        .getTotalPowerByWeek()
        .then(({ data }) => {
          console.log(data);
          if (data.status == 0) {
            this.chartData = {
              columns: ["Time", "PowerTotal"],
              rows: data.data.list
            };
          } else {
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败ww！");
          console.log(data);
        });
    },

    changeDate() {
      const now = new Date().getTime();
      this.data.forEach((item, index) => {
        const date = new Date(now - (6 - index) * 86400000);
        item.name = `${date.getFullYear()}/${date.getMonth() +
          1}/${date.getDate()}`;
      });
    },
    handleListener() {
      bus.$on("collapse", this.handleBus);
    },
    handleBus(msg) {
      setTimeout(() => {
        this.renderChart();
      }, 300);
    },
    renderChart() {
      this.$refs.bar.renderChart();
      this.$refs.line.renderChart();
    }
  }
};
