import API from "@/api";
import Vue from "vue";
export default {
  data() {
    return {
      beginTime: "",
      endTime: "",
      options: [
        {
          value: "选项1",
          label: "黄金糕"
        },
        {
          value: "选项2",
          label: "双皮奶"
        },
        {
          value: "选项3",
          label: "蚵仔煎"
        },
        {
          value: "选项4",
          label: "龙须面"
        },
        {
          value: "选项5",
          label: "北京烤鸭"
        }
      ],
      powerType: "",
      dataSource: "",
      //表格当前页数据
      tableData: [],
      //多选数组
      multipleSelection: [],
      //搜索条件
      criteria: "",
      //下拉菜单选项
      select: "",
      //默认每页数据量
      pagesize: 10,
      //默认高亮行数据id
      highlightId: -1,
      //当前页码
      currentPage: 1,
      //查询的页码
      start: 1,
      //默认数据总数
      totalCount: 1000,
      editUserForm: {
        geNumber: "",
        userState: ""
      },
      addUserForm: {
        geNumber: "",
        geName: "",
        userState: "",
        phone: "",
        email: "",
        password: ""
      },
      dialogFormVisible: false,
      addFormVisible: false,
      formLabelWidth: "120px",
      rules: {
        geNumber: [
          { required: true, message: "请填写工号", trigger: "blur" },
          { min: 7, max: 7, message: "请填写7个字符的工号", trigger: "blur" }
        ],
        geName: [
          { required: true, message: "请填写姓名", trigger: "blur" },
          { min: 2, max: 4, message: "请填写2-4个字符的姓名", trigger: "blur" }
        ],
        password: [
          { required: true, message: "请填写密码", trigger: "blur" },
          {
            min: 6,
            max: 10,
            message: "请填写6-10个字符的密码",
            trigger: "blur"
          }
        ],
        userState: [{ required: true, message: "请选择状态", trigger: "blur" }],
        phone: [
          {
            validator: (rule, value, callback) => {
              let vdt = API.validate.VDATA(value, {
                required: { msg: "请输入手机号" },
                mphone: { msg: "请输入有效的手机号" }
              });
              if (!vdt.result) {
                callback(new Error(vdt.msg));
              } else {
                callback();
              }
            },
            trigger: "blur"
          }
        ],
        email: [
          {
            validator: (rule, value, callback) => {
              let vdt = API.validate.VDATA(value, {
                required: { msg: "请输入邮箱" },
                email: { msg: "请输入有效的邮箱" }
              });
              if (!vdt.result) {
                callback(new Error(vdt.msg));
              } else {
                callback();
              }
            },
            trigger: "blur"
          }
        ]
      },
      list: [
        {
          path: "/1",
          name: "导航一",
          icon: "el-icon-menu",
          children: [
            {
              path: "/1/1",
              name: "导航1-1",
              icon: "el-icon-menu",
              children: [
                {
                  path: "/1/1/1",
                  name: "导航1-1-1",
                  icon: "el-icon-menu"
                },
                {
                  path: "/1/1/2",
                  name: "导航1-1-2",
                  icon: "el-icon-menu"
                },
                {
                  path: "/1/1/3",
                  name: "导航1-1-3",
                  icon: "el-icon-menu"
                }
              ]
            },
            {
              path: "/1/2",
              name: "导航1-2",
              icon: "el-icon-menu"
            }
          ]
        },
        {
          path: "/2",
          name: "导航二",
          icon: "el-icon-menu"
        },
        {
          path: "/3",
          name: "导航一",
          icon: "el-icon-menu",
          children: [
            {
              path: "/3/1",
              name: "导航1-1",
              icon: "el-icon-menu",
              children: [
                {
                  path: "/3/1/1",
                  name: "导航1-1-1",
                  icon: "el-icon-menu"
                },
                {
                  path: "/3/1/2",
                  name: "导航1-1-2",
                  icon: "el-icon-menu"
                },
                {
                  path: "/3/1/3",
                  name: "导航1-1-3",
                  icon: "el-icon-menu"
                }
              ]
            },
            {
              path: "/3/2",
              name: "导航1-2",
              icon: "el-icon-menu"
            }
          ]
        },
        {
          path: "/4",
          name: "导航一",
          icon: "el-icon-menu",
          children: [
            {
              path: "/4/1",
              name: "导航1-1",
              icon: "el-icon-menu",
              children: [
                {
                  path: "/4/1/1",
                  name: "导航1-1-1",
                  icon: "el-icon-menu"
                },
                {
                  path: "/4/1/2",
                  name: "导航1-1-2",
                  icon: "el-icon-menu"
                },
                {
                  path: "/4/1/3",
                  name: "导航1-1-3",
                  icon: "el-icon-menu"
                }
              ]
            },
            {
              path: "/4/2",
              name: "导航1-2",
              icon: "el-icon-menu"
            }
          ]
        }
      ]
    };
  },
  created() {
    this.loadData(this.criteria, this.currentPage, this.pagesize);
  },
  methods: {
    //从服务器读取数据
    loadData: function(criteria, pageNum, pageSize) {
      let params = {
        parameter: criteria,
        pageNum: pageNum,
        pageSize: pageSize
      };
      API.userUtil
        .getData(params)
        .then(({ data }) => {
          console.log(data);
          if (data.status == 0) {
            this.tableData = data.data.userData;
            this.totalCount = data.data.number;
          } else {
            //this.$Message.error(data.msg);
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败ww！");
          console.log(data);
        });
    },

    //多选响应
    handleSelectionChange: function(val) {
      this.multipleSelection = val;
    },

    //点击行响应
    handleclick: function(row, event, column) {
      this.highlightId = row.id;
    },

    //编辑
    handleEdit: function(index, row) {
      this.editUserForm.geNumber = row.geNumber;
      this.editUserForm.userState = row.userState;
      this.dialogFormVisible = true;
    },

    //修改
    editSubmitForm(editUserForm) {
      let params = {
        geNumber: this.editUserForm.geNumber,
        userState: this.editUserForm.userState
      };
      this.$refs[editUserForm].validate(valid => {
        if (valid) {
          API.userUtil
            .updateUser(params)
            .then(({ data }) => {
              if (data.data == true) {
                this.dialogFormVisible = false;
                this.loadData(this.criteria, this.currentPage, this.pagesize);
              } else {
                this.$message("请求失败！");
              }
            })
            .catch(data => {
              this.$message("请求失败！");
              console.log(data);
            });
        } else {
          this.$message.error("请求失败！");
          return false;
        }
      });
    },

    //添加用户
    addSubmitForm(addUserForm) {
      let params = {
        geNumber: this.addUserForm.geNumber,
        geName: this.addUserForm.geName,
        userState: this.addUserForm.userState,
        phone: this.addUserForm.phone,
        email: this.addUserForm.email,
        password: this.addUserForm.password
      };

      this.$refs[addUserForm].validate(valid => {
        if (valid) {
          API.userUtil
            .addUser(params)
            .then(({ data }) => {
              if (data.data == true) {
                this.addFormVisible = false;
                this.$message("添加成功!");
                this.loadData(this.criteria, this.currentPage, this.pagesize);
              } else {
                this.$message("请求失败！");
              }
            })
            .catch(data => {
              this.$message("请求失败！");
              console.log(data);
            });
        } else {
          return false;
        }
      });
    },

    //单行删除
    handleDelete: function(index, row) {
      let params = { geNumber: row.geNumber };
      this.$confirm(
        "此操作将永久删除“" + row.geNumber + "”, 是否继续?",
        "提示",
        {
          confirmButtonText: "确定",
          cancelButtonText: "取消",
          type: "warning"
        }
      )
        .then(() => {
          API.userUtil
            .deleteUser(params)
            .then(({ data }) => {
              if (data.data == true) {
                this.$message({ type: "success", message: "删除成功!" });
                this.loadData(this.criteria, this.currentPage, this.pagesize);
              } else {
                this.$message({ type: "info", message: "删除失败" });
              }
            })
            .catch(data => {
              this.$message("请求失败！");
            });
          this.$message({ type: "success", message: "删除成功!" });
        })
        .catch(() => {
          this.$message({ type: "info", message: "已取消删除" });
        });
    },

    //搜索
    search: function() {
      console.log(this.select);
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    },
    add: function() {
      this.addFormVisible = true;
    },
    exportUser() {
      this.$message("等待开发中！！！");
    },
    /*
		        //多项删除
		        deletenames: function(){
		        	if(this.multipleSelection.length==0)
		        		return;
		        	var array = [];
		        	this.multipleSelection.forEach((item) = > {
		        		array.push(item.id);
			        })
					this.$http.post('../delete',{"array":array},{emulateJSON: true}).then(function(res){
						this.loadData(this.criteria, this.currentPage, this.pagesize);
		            },function(){
		                console.log('failed');
		            });
		        },
		      */
    //改变当前点击的行的class，高亮当前行
    tableRowClassName: function(row, index) {
      if (row.id == this.highlightId) {
        return "info-row";
      }
    },

    //每页显示数据量变更
    handleSizeChange: function(val) {
      this.pagesize = val;
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    },

    //页码变更
    handleCurrentChange: function(val) {
      this.currentPage = val;
      this.loadData(this.criteria, this.currentPage, this.pagesize);
    }
  }
};
