import bus from "../common/bus";
import API from "@/api";
import Vue from "vue";
export default {
  name: "dashboard",
  data() {
    this.chartSettings = {
      labelMap: {
        PowerTotal: "同安用电总量(KWh)"
      }
    };

    return {
      recordTotal: "",
      userTotal: "",
      lastIP: "",
      lastTime: "",
      name: localStorage.getItem("username"),
      chartData: [],
      todoList: [
        {
          title: "7.30号更新",
          status: false
        }
      ],
      data: []
    };
  },
  components: {},
  computed: {
    role() {
      return localStorage.getItem("rolename")
    }
  },
  created() {
    this.getTimeAndIp();
    this.fillData();
    this.handleListener();
  },
  activated() {
    this.handleListener();
  },
  deactivated() {
    window.removeEventListener("resize", this.renderChart);
    bus.$off("collapse", this.handleBus);
  },
  methods: {
    getTimeAndIp: function() {
      
      let params = {
        geNumber: localStorage.getItem("username")
      };
      API.userUtil
        .getTimeAndIp(params)
        .then(({ data }) => {
          
          if (data.status == 0) {
            this.lastIP = data.data.ip;
            this.lastTime = data.data.time;
            this.recordTotal = data.data.recordTotal;
            this.userTotal = data.data.userTotal;
          } else {
            //this.$Message.error(data.msg);
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败！");
          console.log(data);
        });
    },

    fillData: function() {
      API.powerUtil
        .getTotalPowerByWeek()
        .then(({ data }) => {
          
          if (data.status == 0) {
            this.chartData = {
              columns: ["Time", "PowerTotal"],
              rows: data.data.list
            };
          } else {
            this.$message("请求失败！");
          }
        })
        .catch(data => {
          this.$message("请求失败ww！");
          console.log(data);
        });
    },

    changeDate() {
      const now = new Date().getTime();
      this.data.forEach((item, index) => {
        const date = new Date(now - (6 - index) * 86400000);
        item.name = `${date.getFullYear()}/${date.getMonth() +
          1}/${date.getDate()}`;
      });
    },
    handleListener() {
      bus.$on("collapse", this.handleBus);
    },
    handleBus(msg) {
      setTimeout(() => {
        this.renderChart();
      }, 300);
    },
    renderChart() {
      //this.$refs.bar.renderChart();
     // this.$refs.line.renderChart();
    }
  }
};
